import React, { useState, useEffect } from "react";
import IndivisualContent from "./indivisual-content";
import "./style.css";

import { Link } from "react-router-dom";

function ContentCarousel(props) {
  const [pixel, transformPixel] = useState(0);

  const updatePixel = (pixel) => {
    const track = document.getElementById(`${props.arrayOf}`);
    track.style.transform = `translateX(-${pixel}vw)`;
  };

  useEffect(() => {
    updatePixel(pixel);
  });

  const movieItem = (content) => {
    return (
      <div className="card-container">
        <section className="card">
          <img
            src={`https://image.tmdb.org/t/p/w500${
                content.backdrop_path
                ? content.backdrop_path
                : props.para.poster_path
            }`}
          />
          <p>{props.para.title ? props.para.title : props.para.name}</p>
        </section>
      </div>
    );
  };

  return (
    <div className="array-of-content carausal-container">
      <div className="carausal-container-inner">
        <div className="track" id={props.arrayOf}>
          {props.para.map((indiContent) =>
            indiContent.backdrop_path ? (
              <Link
                to={`/browse/${indiContent.id}/${Boolean(
                  indiContent["release_date"]
                )}`}
              >
                {movieItem(indiContent)}
              </Link>
            ) : null
          )}
        </div>
      </div>
      <div className="nav">
        <button
          className="prev"
          onClick={() => {
            if (pixel > 0) {
              transformPixel((prevPixel) => prevPixel - 50);
            }
          }}
        >
          <svg viewBox="0 0 24 24">
            <path d="M11.56 5.56L10.5 4.5 6 9l4.5 4.5 1.06-1.06L8.12 9z"></path>
          </svg>
        </button>
        <button
          className="next"
          onClick={() => {
            if (
              pixel <
              document.getElementById(props.arrayOf).clientWidth / 6
            ) {
              transformPixel((prevPixel) => prevPixel + 50);
            }
          }}
        >
          <svg viewBox="0 0 24 24">
            <path d="M11.56 5.56L10.5 4.5 6 9l4.5 4.5 1.06-1.06L8.12 9z"></path>
          </svg>
        </button>
      </div>
    </div>
  );
}

export default ContentCarousel;
