import React, { useState, useEffect } from "react";
import { css } from "@emotion/css";
import { Link, useNavigate } from "react-router-dom";

function ContentCarousel({ contentList }) {
  const navigate = useNavigate();

  return (
    <div className={carouselContainer}>
      {contentList.map((content) =>


            <div className={css(contentCard)} onClick={() => {
              navigate(`/browse/${content.id}/${Boolean(content["release_date"])}`)
            }}>
              <img
                src={`https://image.tmdb.org/t/p/w500${
                  content.backdrop_path
                    ? content.backdrop_path
                    : content.poster_path
                }`}
              />
              <p>{content.title ? content.title : content.name}</p>
            </div>


      )}
    </div>
  );
}

export default ContentCarousel;

const carouselContainer = css`
  height: 100%;
  padding: 120px 25px;
  overflow: scroll;
  display: flex;
  flex-wrap: wrap;
  justify-content: space-between;
`;

const contentCard = css`
  width: 250px;
  height: auto;
  img {
    height: 100%;
    width: 100%;
  }
`;
